mapper.select:
SELECT *
  FROM tb_user
 WHERE user_mail = #{userMail}
   AND user_pw = #{userPw}

SELECT *
  FROM tb_user
 WHERE user_name = #{userName}
   AND user_registration = #{userRegistration}


SELECT *
  FROM tb_user 
 WHERE user_mail = #{userMail} 
   AND user_registration = #{userRegistration}

SELECT user_mail
  FROM tb_user
 WHERE user_mail = #{userMail}

mapper.update:
UPDATE tb_user
   SET user_pw = #{userPw}
 WHERE user_mail = #{userMail}
